import React from 'react';
import {Grid} from "@mui/material";
import {useForm, Form} from "../../components/useForm";
import Controls from "../../components/controls/Controls";
import * as supplierService from "../../services/supplierService"

const initialFValues = {
    id: 0,
    supName: '',
    address1:'',
    address2:'',
    address3:'',
    email:'',
    contact:'',
    gender:'male',
    departmentId:'',
}

export default function SupplierForm(props) {

    const {addOrEdit} = props

    const validate = (fieldValues = values) => {
        let temp = {...errors}
        if('supName' in fieldValues) {
            temp.supName = fieldValues.supName ? "" : "This field is required"
        }
        if('contact' in fieldValues) {
            temp.contact = fieldValues.contact.length >= 10 ? "" : "This field is required"
        }
        if('email' in fieldValues) {
            temp.email = (/$^|.+@.+..+/).test(values.email) ? "" : "Email is not valid"
        }
        // if('departmentId' in fieldValues) {
        //     temp.departmentId = values.departmentId.length != 0 ? "" : "Thid field is required"
        // }
        setErrors({
            ...temp
        })
        if(fieldValues == values) {
            return Object.values(temp).every(x => x == "")
        }
    }

    const {
        values,
        setValues,
        errors,
        setErrors,
        handleInputChange,
        resetForm
    } = useForm(initialFValues, true, validate)

    const handleSubmit = e =>{
        e.preventDefault();

        if(validate()) {
            supplierService.insertSupplier(values);
            resetForm();
        }
    }

    return (
        <Form onSubmit={handleSubmit}>
            <Grid container direction="row">
                <Grid item xs={6}>
                    <Controls.Input
                        name="supName"
                        label="Supplier Name"
                        value={values.supName}
                        onChange={handleInputChange}
                        error={errors.supName}
                    />

                    <Controls.Input
                        name="address1"
                        label="Address Line 1"
                        value={values.address1}
                        onChange={handleInputChange}
                    />

                    <Controls.Input
                        name="address2"
                        label="Address Line 2"
                        value={values.address2}
                        onChange={handleInputChange}
                    />

                    <Controls.Input
                        name="address3"
                        label="Address Line 3"
                        value={values.address3}
                        onChange={handleInputChange}
                    />
                </Grid>
                <Grid item xs={6}>
                    <Controls.Input
                        name="email"
                        label="Email"
                        value={values.email}
                        onChange={handleInputChange}
                        error={errors.email}
                    />

                    <Controls.Input
                        name="contact"
                        label="Contact (Number 1, Number 2, ....)"
                        value={values.contact}
                        onChange={handleInputChange}
                        error={errors.contact}
                    />

                    <Controls.Button
                        style={{marginLeft: 10}}
                        type="submit"
                        text="Submit"
                    />

                    <Controls.Button
                        style={{marginLeft: 10}}
                        color="warning"
                        text="Reset"
                        onClick={resetForm}
                    />
                </Grid>
            </Grid>
        </Form>
    );
}
