import React, {useEffect, useState} from 'react';
import SupplierForm from "./SupplierForm";
import PeopleAltTwoToneIcon from "@mui/icons-material/PeopleAltTwoTone";
import PageHeader from "../../components/PageHeader";
import {Paper, TableBody, TableCell, TableRow} from "@mui/material";
import useTable from "../../components/useTable"
import * as supplierService from "../../services/supplierService"
import {makeStyles} from "@mui/styles";

const useStyles = makeStyles(theme =>({
    pageContent: {
        margin: 10,
        padding: 20,
    }
}))

export default function Supplier() {

    const classes = useStyles();
    const [records, setRecords] = useState(supplierService.getAllSuppliers())

    const {
     TblContainer
    } = useTable();

    return (
        <>
            <PageHeader
                title="Supplier"
                subTitle="View/ Add / Update / Delete Suppliers"
                icon={<PeopleAltTwoToneIcon fontSize="large"/>}
            />
            <Paper className={classes.pageContent}/*style={{margin: 'auto', padding: 20, width: '60%'}}*/>
                {/*<SupplierForm/>*/}
                <TblContainer>
                    <TableBody>
                        {
                            records.map(item =>
                                (<TableRow>
                                    <TableCell>{item.name}</TableCell>
                                    <TableCell>{item.address1}</TableCell>
                                    <TableCell>{item.address2}</TableCell>
                                    <TableCell>{item.address3}</TableCell>
                                    <TableCell>{item.contact}</TableCell>
                                </TableRow>)
                            )
                        }
                    </TableBody>
                </TblContainer>
            </Paper>
        </>
    );
}
