import React from 'react';
import {Grid} from "@mui/material";
import {useForm, Form} from "../../components/useForm";
import Controls from "../../components/controls/Controls";

const initialFValues = {
    id: 0,
    supName: '',
    address1:'',
    address2:'',
    address3:'',
    email:'',
    contact:'',
    gender:'male',
    departmentId:'',
}

function SupplierForm(props) {

    const validate = () => {
        let temp = {}
        temp.supName = values.supName ? "" : "This field is required"
        temp.address1 = values.address1.length > 4 ? "" : "This field is required"
        // temp.email = (/\\S+@\\S+\\.\\S+/).test(values.email) ? "" : "Email is not valid"
        setErrors({
            ...temp
        })
        return Object.values(temp).every(x => x == "")
    }

    const {
        values,
        setValues,
        handleInputChange,
        errors,
        setErrors
    } = useForm(initialFValues)

    const handleSubmit = e =>{
        e.preventDefault();
        console.log("AAAA - ", values)
        if(validate()) {
            window.alert("AAAAA")
        }
    }

    return (
        <Form onSubmit={handleSubmit}>
            <Grid container direction="row">
                <Grid item xs={6}>
                    <Controls.Input
                        name="supName"
                        label="Supplier Name"
                        value={values.supName}
                        onChange={handleInputChange}
                    />

                    <Controls.Input
                        name="address1"
                        label="Address Line 1"
                        value={values.address1}
                        onChange={handleInputChange}
                    />

                    <Controls.Input
                        name="address2"
                        label="Address Line 2"
                        value={values.address2}
                        onChange={handleInputChange}
                    />
                    <Controls.Input
                        name="address3"
                        label="Address Line 3"
                        value={values.address3}
                        onChange={handleInputChange}
                    />
                </Grid>
                <Grid item xs={6}>
                    <Controls.Input
                        name="email"
                        label="Email"
                        value={values.email}
                        onChange={handleInputChange}
                    />

                    <Controls.Button
                        style={{marginLeft: 10}}
                        type="submit"
                        text="Submit"
                    />

                    <Controls.Button
                        style={{marginLeft: 10}}
                        color="error"
                        text="Reset"
                    />

                </Grid>
            </Grid>
        </Form>
    );
}

export default SupplierForm;
