import React, {useEffect} from 'react';
import {Grid} from "@mui/material";
import {useForm, Form} from ".././../components/useForm";
import Controls from "../../components/controls/Controls";

const initialFValues = {
    id: 0,
    firstName: '',
    lastName:'',
    userName:'',
    password:'',
    level:'',
    contact:''
}

const levelValues = [
    {id: 'admin', title: 'Admin'},
    {id: 'user', title: 'User'},
]

export default function SupplierForm(props) {

    const {addOrEdit, recordForEdit} = props

    const validate = (fieldValues = values) => {
        let temp = {...errors}
        if('supName' in fieldValues) {
            temp.supName = fieldValues.supName ? "" : "This field is required"
        }
        if('contact' in fieldValues) {
            temp.contact = fieldValues.contact.length == 10 ? "" : "This field is required"
        }
        if('email' in fieldValues) {
            temp.email = (/$^|.+@.+..+/).test(values.email) ? "" : "Email is not valid"
        }
        // if('departmentId' in fieldValues) {
        //     temp.departmentId = values.departmentId.length != 0 ? "" : "Thid field is required"
        // }
        setErrors({
            ...temp
        })
        if(fieldValues == values) {
            return Object.values(temp).every(x => x == "")
        }
    }

    const {
        values,
        setValues,
        errors,
        setErrors,
        handleInputChange,
        resetForm
    } = useForm(initialFValues, true, validate)

    const handleSubmit = e =>{
        e.preventDefault();

        // if(validate()) {
            addOrEdit(values, resetForm);
        // }
    }

    useEffect(() =>{
        if(recordForEdit != null){
            setValues({
                ...recordForEdit
            })
        }
    }, [recordForEdit])

    return (
        <Form onSubmit={handleSubmit}>
            <Grid container direction="row">
                <Grid item xs={6}>
                    <Controls.Input
                        name="firstName"
                        label="First Name"
                        value={values.firstName}
                        onChange={handleInputChange}
                        error={errors.firstName}
                    />

                    <Controls.Input
                        name="lastName"
                        label="Last Name"
                        value={values.lastName}
                        onChange={handleInputChange}
                    />

                    <Controls.Input
                        name="userName"
                        label="UserName"
                        value={values.userName}
                        onChange={handleInputChange}
                        disabled={recordForEdit ? true : false}
                    />

                    <Controls.Input
                        name="password"
                        label="Password"
                        value={values.password}
                        onChange={handleInputChange}
                    />
                </Grid>

                <Grid item xs={6}>
                    <Controls.Input
                        name="contsct"
                        label="Contact Number"
                        value={values.contact}
                        onChange={handleInputChange}
                    />

                    <Controls.RadioGroup
                        name="level"
                        label="User Level"
                        value={values.level}
                        onChange={handleInputChange}
                        items={levelValues}
                    />
                    <Controls.Button
                        style={{marginLeft: 10}}
                        type="submit"
                        text="Submit"
                    />

                    <Controls.Button
                        style={{marginLeft: 10}}
                        color="inherit"
                        text="Reset"
                        onClick={resetForm}
                    />
                </Grid>
            </Grid>
        </Form>
    );
}
