import React, {useEffect, useState} from 'react';
import {Grid} from "@mui/material";
import {useForm, Form} from ".././../components/useForm";
import Controls from "../../components/controls/Controls";
import {InputAdornment, Paper, TableBody, TableCell, TableRow, Toolbar} from "@mui/material";
import {makeStyles} from "@mui/styles";
import useTable from "../../components/useTable"

const initialFValues = {
    id: 0,
    supName: '',
    address1:'',
    address2:'',
    address3:'',
    email:'',
    contact:'',
    gender:'male',
    departmentId:'',
}

const useStyles = makeStyles(theme =>({
    pageContent: {
        margin: 20,
        padding: 20
    },
    searchInput:{
        width:'75%'
    },
    newButton:{
        position: 'absolute',
        left: 100,
        spacing:24
    }
}));

const headCells = [
    {id: 'itemName', label:'Item Name'},
    {id: 'quantity', label:'Quantity'},
    {id: 'amount', label:'Amount'},
]

export default function InvoiceForm(props) {

    const classes = useStyles();
    const {addOrEdit, recordForEdit, recordList} = props
    const [filterFn, setFilterFn] = useState({fn: items => {return items;}})

    const validate = (fieldValues = values) => {
        let temp = {...errors}
        // if('supName' in fieldValues) {
        //     temp.supName = fieldValues.supName ? "" : "This field is required"
        // }
        // if('contact' in fieldValues) {
        //     temp.contact = fieldValues.contact.length >= 10 ? "" : "This field is required"
        // }
        if('email' in fieldValues) {
            temp.email = (/$^|.+@.+..+/).test(values.email) ? "" : "Email is not valid"
        }
        // if('departmentId' in fieldValues) {
        //     temp.departmentId = values.departmentId.length != 0 ? "" : "Thid field is required"
        // }
        setErrors({
            ...temp
        })
        if(fieldValues == values) {
            return Object.values(temp).every(x => x == "")
        }
    }

    const {
        TblContainer,
        TblHead,
        TblPagination,
        recordsAfterPagingAndSorting
    } = useTable(recordList, headCells, filterFn);


    const {
        values,
        setValues,
        errors,
        setErrors,
        handleInputChange,
        resetForm
    } = useForm(initialFValues, true, validate)

    const handleSubmit = e =>{
        e.preventDefault();

        if(validate()) {
            addOrEdit(values, resetForm);
        }
    }

    // useEffect(() =>{
    //     if(recordForEdit != null){
    //         setValues({
    //             ...recordForEdit
    //         })
    //     }
    // }, [recordForEdit])

    return (
        // <Paper className={classes.pageContent}>
        <Paper>
        <Form onSubmit={handleSubmit}>
            <Grid container direction="row">
                <Grid item xs={6}>
                    <Controls.Input
                        name="supName"
                        label="Supplier Name"
                        value={values.supName}
                        onChange={handleInputChange}
                        error={errors.supName}
                    />

                    <Controls.Input
                        name="address1"
                        label="Address Line 1"
                        value={values.address1}
                        onChange={handleInputChange}
                    />

                    <Controls.Input
                        name="address2"
                        label="Address Line 2"
                        value={values.address2}
                        onChange={handleInputChange}
                    />

                    <Controls.Input
                        name="address3"
                        label="Address Line 3"
                        value={values.address3}
                        onChange={handleInputChange}
                    />
                </Grid>
                <Grid item xs={6}>
                    <Controls.Input
                        name="email"
                        label="Email"
                        value={values.email}
                        onChange={handleInputChange}
                        error={errors.email}
                    />

                    <Controls.Input
                        name="contact"
                        label="Contact (Number 1, Number 2, ....)"
                        value={values.contact}
                        onChange={handleInputChange}
                        error={errors.contact}
                    />

                    <Controls.Button
                        style={{marginLeft: 10}}
                        type="submit"
                        text="Submit"
                    />

                    <Controls.Button
                        style={{marginLeft: 10}}
                        color="inherit"
                        text="Reset"
                        onClick={resetForm}
                    />
                </Grid>
            </Grid>
        </Form>
        <TblContainer>
            <TblHead/>
            <TableBody>
                {
                    recordList.map(item =>
                        (<TableRow>
                            <TableCell>{item.itemName}</TableCell>
                            <TableCell>{item.quantity}</TableCell>
                            <TableCell>{item.amount}</TableCell>
                        </TableRow>)
                    )
                }
            </TableBody>
            </TblContainer>
</Paper>

    );
}
