import React, {useEffect, useState} from 'react';
import {makeStyles} from "@mui/styles";
import {InputAdornment, Paper, TableBody, TableCell, TableRow, Toolbar} from "@mui/material";
import moment from 'moment';

const useStyles = makeStyles(theme =>({
    pageContent: {
        margin: 20,
        padding: 20
    },
    searchInput:{
        width:'75%'
    },
    newButton:{
        position: 'absolute',
        left: 100,
        spacing:24
    }
}));

export default function ViewDetail(props) {

    const {recordForView} = props
    const classes = useStyles();

    return (
        <Paper className={classes.pageContent}>      
            <TblContainer>
                <TblHead/>
                <TableBody>
                    {
                        recordForView.map(item =>
                            (<TableRow>
                                <TableCell>{item.orderId}</TableCell>
                                <TableCell>{moment(item.orderDate).format('DD/MMM/yyyy')}</TableCell>
                                <TableCell>{item.customerName}</TableCell>
                                <TableCell>{item.totalAmount}</TableCell>
                            </TableRow>)
                        )
                    }
                </TableBody>
            </TblContainer>
            <TblPagination/>
        </Paper>
    );
}
