import React,{useState, useEffect} from 'react';
import {Grid, TextField} from "@mui/material";
import {useForm, Form} from "../useForm";
import Controls from "../controls/Controls";

const initialFValues = {
    id: 0,
    fullName: '',
    email:'',
    mobile:'',
    city:'',
    gender:'male',
    departmentId:'',
    hireDate:new Date(),
    isPermanent:false
}

function SupplierForm(props) {

    const {
        values,
        setValues,
        handleInputChange
    } = useForm(initialFValues)

    return (
        <Form>
            <Grid container>
                <Grid item xs={6}>
                    <Controls.Input
                        name="fullName"
                        label="Full Name"
                        value={values.fullName}
                        onChange={handleInputChange}
                    />

                    <Controls.Input
                        name="email"
                        label="Email"
                        value={values.email}
                        onChange={handleInputChange}
                    />

                    <Controls.Select
                        name="departmentId"
                        label="Department"
                        value={values.departmentId}
                        onChange={handleInputChange}
                        options={null}
                    />
                </Grid>
            </Grid>
        </Form>
    );
}

export default SupplierForm;
