const KEYS ={
    suppliers: 'suppliers',
    supplierId: 'supplierId'
}

export const getDepartmentCollection = () => ([
    {id: '1', title:'Development'},
    {id: '2', title:'Marketing'},
    {id: '3', title:'IT'},
    {id: '4', title:'Accounting'},
    {id: '5', title:'HR'},
    {id: '6', title:'Finance'}
])

export function insertSupplier(data){
    let employees = getAllSuppliers();
    employees.push(data);
    localStorage.setItem(KEYS.suppliers, JSON.stringify(data))
}

export function generateEmployeeId(){
    if(localStorage.getItem(KEYS.suppliers) == null){
        localStorage.setItem(KEYS.suppliers, JSON.stringify([]))
    }
}

export function getAllSuppliers(data){

    if(localStorage.getItem(KEYS.suppliers) == null){
        localStorage.setItem(KEYS.suppliers, JSON.stringify([]))
    }
    return JSON.parse(localStorage.getItem(KEYS.employees));
}
