import React from 'react';
import {Grid} from "@mui/material";
import {useForm, Form} from "../../components/useForm";
import Controls from "../../components/controls/Controls";
import * as supplierService from "../../services/supplierService"

const initialFValues = {
    id: 0,
    supName: '',
    address1:'',
    address2:'',
    address3:'',
    email:'',
    contact:'',
    gender:'male',
    departmentId:'',
}

function SupplierForm(props) {

    const {
        values,
        setValues,
        handleInputChange
    } = useForm(initialFValues)

    return (
        <Form>
            <Grid container direction="row">
                <Grid item xs={6}>
                    <Controls.Input
                        name="supName"
                        label="Supplier Name"
                        value={values.supName}
                        onChange={handleInputChange}
                    />

                    <Controls.Input
                        name="address1"
                        label="Address Line 1"
                        value={values.address1}
                        onChange={handleInputChange}
                    />

                    <Controls.Input
                        name="address2"
                        label="Address Line 2"
                        value={values.address2}
                        onChange={handleInputChange}
                    />
                    <Controls.Input
                        name="address3"
                        label="Address Line 3"
                        value={values.address3}
                        onChange={handleInputChange}
                    />
                </Grid>
                <Grid item xs={6}>
                    <Controls.Input
                        name="email"
                        label="Email"
                        value={values.email}
                        onChange={handleInputChange}
                    />

                    <Controls.Input
                        name="contact"
                        label="Contact Numbers (Number 1, Number 2, Number 3, Etc)"
                        value={values.contact}
                        onChange={handleInputChange}
                    />

                    <Controls.DatePicker
                        name="hireDate"
                        label="Hire Date"
                        value={values.hireDate}
                        onChange={handleInputChange}
                    />
                    <Controls.Select
                        name="departmentId"
                        label="Department"
                        value={values.departmentId}
                        onChange={handleInputChange}
                        options={supplierService.getDepartmentCollection()}
                    />
                </Grid>
            </Grid>
        </Form>
    );
}

export default SupplierForm;
