import React, {useEffect, useState} from 'react';
import {Grid} from "@mui/material";
import {useForm, Form} from ".././../components/useForm";
import Controls from "../../components/controls/Controls";
import {InputAdornment, Paper, TableBody, TableCell, TableRow, Toolbar} from "@mui/material";
import {makeStyles} from "@mui/styles";
import useTable from "../../components/useTable"

const initialFValues = {
    id: 0,
    customerId: '',
    customerName: '',
    itemId:'',
    itemName:'',
    quantity:0,
    amount:0,
}

const useStyles = makeStyles(theme =>({
    pageContent: {
        margin: 20,
        padding: 20
    },
    searchInput:{
        width:'75%'
    },
    newButton:{
        position: 'absolute',
        left: 100,
        spacing:24
    }
}));

const headCells = [
    {id: 'itemName', label:'Item Name'},
    {id: 'quantity', label:'Quantity'},
    {id: 'amount', label:'Amount'},
]

export default function InvoiceForm(props) {

    const classes = useStyles();
    const {addOrEdit, recordForEdit, addToTable, recordList, customerOptions, itemOptions} = props
    const [filterFn, setFilterFn] = useState({fn: items => {return items;}})

    const validate = (fieldValues = values) => {
        let temp = {...errors}
        // if('supName' in fieldValues) {
        //     temp.supName = fieldValues.supName ? "" : "This field is required"
        // }
        // if('contact' in fieldValues) {
        //     temp.contact = fieldValues.contact.length >= 10 ? "" : "This field is required"
        // }
        if('email' in fieldValues) {
            temp.email = (/$^|.+@.+..+/).test(values.email) ? "" : "Email is not valid"
        }
        // if('departmentId' in fieldValues) {
        //     temp.departmentId = values.departmentId.length != 0 ? "" : "Thid field is required"
        // }
        setErrors({
            ...temp
        })
        if(fieldValues == values) {
            return Object.values(temp).every(x => x == "")
        }
    }

    const {
        TblContainer,
        TblHead,
        TblPagination,
        recordsAfterPagingAndSorting
    } = useTable(recordList, headCells, filterFn);


    const {
        values,
        setValues,
        errors,
        setErrors,
        handleInputChange,
        resetForm
    } = useForm(initialFValues, true, validate)

    const handleSubmit = e =>{
        console.log('recordList', recordList)
        // e.preventDefault();

        // if(validate()) {
            addOrEdit(recordList, resetForm);
        // }
    }

    const addValue = e =>{
        addToTable(values, resetForm);
    }

    // useEffect(() =>{
    //     if(recordForEdit != null){
    //         setValues({
    //             ...recordForEdit
    //         })
    //     }
    // }, [recordForEdit])
    return (

        // <Paper className={classes.pageContent}>
        <Paper>
        <Form onSubmit={handleSubmit}>
            <Grid container direction="row">
                <Grid item xs={8}>
                <Controls.Select
                        name="customerId"
                        label="Select Customer"
                        options={customerOptions}
                        value={values.customerId}
                        onChange={handleInputChange}
                        error={errors.customerId}
                    />

                    <Controls.Select
                        name="itemId"
                        label="Select Item"
                        options={itemOptions}
                        value={values.itemId}
                        onChange={handleInputChange}
                        error={errors.itemId}
                    />

                    <Controls.Input
                        name="quantity"
                        label="Quantity"
                        value={values.quantity}
                        onChange={handleInputChange}
                    />

                    <Controls.Input
                        name="amount"
                        label="Amount"
                        value={values.amount}
                        onChange={handleInputChange}
                    />
                </Grid>

                <Grid item xs={4}>
                    <Controls.Button
                        style={{margin: 10, marginTop:'70px', maxWidth: '60px', minWidth: '100px'}}
                        text="Add"
                        onClick={addValue}
                    />
                    <Controls.Button
                        style={{margin: 10}}
                        // type="submit"
                        onClick={handleSubmit}
                        text="Submit"
                    />

                    <Controls.Button
                        style={{margin: 10, maxWidth: '60px', minWidth: '100px'}}
                        color="inherit"
                        text="Reset"
                        onClick={resetForm}
                    />
                </Grid>
            </Grid>
        </Form>
        {recordList.length > 0 ? 
        <TblContainer>
            <TblHead/>
            <TableBody>
                {   
                    recordList.map(item =>
                        (<TableRow key={item.order.id}>
                            <TableCell>{item.order.itemName}</TableCell>
                            <TableCell>{item.order.quantity}</TableCell>
                            <TableCell>{item.order.amount}</TableCell>
                        </TableRow>)
                    )
                }
            </TableBody>
            </TblContainer>
        : null}
        </Paper>

    );
}
