import React, {useEffect, useState} from 'react';
import SupplierForm from "./SupplierForm";
import PeopleAltTwoToneIcon from "@mui/icons-material/PeopleAltTwoTone";
import PageHeader from "../../components/PageHeader";
import {Paper, TableBody, TableCell, TableRow} from "@mui/material";
import useTable from "../../components/useTable"
import axios from "axios";
// import {makeStyles} from "@mui/styles";

// const useStyles = makeStyles(theme =>({
//     pageContent: {
//         margin: 10,
//         padding: 20,
//     }
// }))

export default function Supplier() {

    // const classes = useStyles();

    const [supplier, setSupplier] = useState();

    const getSupplierData = async () =>{
        try{
            const data = await axios.get('http://localhost:8080/api/v1/supplier/all');
            console.log('data.data', data.data.data);
            setSupplier(data.data.data);
        } catch (e){

        }
    };

    useEffect(() => {
        getSupplierData();
    }, [])

    const {
     TblContainer
    } = useTable();

    return (
        <>
            <PageHeader
                title="Supplier"
                subTitle="View/ Add / Update / Delete Suppliers"
                icon={<PeopleAltTwoToneIcon fontSize="large"/>}
            />
            <Paper elevation={24}/*className={classes.pageContent}*/style={{margin: 'auto', padding: 20, width: '60%'}}>
                {/*<SupplierForm/>*/}
                <TblContainer>
                    <TableBody>
                        {
                            supplier.map(item =>
                                (<TableRow key={item.id}>
                                    <TableCell>{item.name}</TableCell>
                                    <TableCell>{item.address1}</TableCell>
                                    <TableCell>{item.address2}</TableCell>
                                    <TableCell>{item.address3}</TableCell>
                                    <TableCell>{item.contact}</TableCell>
                                </TableRow>)
                            )
                        }
                    </TableBody>
                </TblContainer>
            </Paper>
        </>
    );
}
